package ForLoop;
import java.util.*;
public class SolidPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Scanner sc= new Scanner(System.in);
System.out.println("Enter the number for print a star");
int n=sc.nextInt();
for(int i=1;i<=n-1;i++) {
	for(int j=1;j<=n;j++) {
		System.out.print("1");
	}
	System.out.println();
}

		
		
		
	}

}
