package switchOperation;

public class SwitchBasic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =10;
		switch(a){
		case 1:
			System.out.println("case 1");
			break;
			
		case 10:
			System.out.println("case 10");
			break;
			default:
				System.out.println("invalid");
		}
		}

	}


